mapper.select:
select * from reservation a
left join room_detail b on a.room_no = b.room_no
where a.guest_id = #{guestId}

mapper.update:
UPDATE RESERVATION
SET status = 'CHECKIN'
where guest_id = #{guestId}

UPDATE RESERVATION
SET status = 'CONFIRM'
where guest_name = #{guestName}

UPDATE RESERVATION
SET status = #{status}
where guest_id = #{guestId}
